mapper.update:
UPDATE member
SET last_login_date = sysdate
WHERE member_id = #{memberId}

mapper.select:
SELECT rnum, member_id, name, nickname, email, tel, birthday, member_level, kakao_no, member_role, status, last_login_date, point, reg_date, address1, address2 FROM ( SELECT rownum as rnum, member_id, name, nickname, email, tel, birthday, member_level, kakao_no, member_role, status, last_login_date, point, reg_date, address1, address2 FROM ( SELECT m.member_id, m.name, nickname, email, m.tel, birthday, member_level, kakao_no, member_role, status, last_login_date, point, reg_date, address1, address2 FROM member m, address a WHERE m.member_id = a.member_id(+) AND m.member_role = 'ROLE_USER' AND (a.default_address = '1' OR a.default_address IS NULL)
AND m.member_id LIKE '%'||#{searchTypeMember.searchMemberId}||'%'
AND m.name LIKE '%'||#{searchTypeMember.searchName}||'%'
AND member_level IN
#{memberLevel}
ORDER BY m.reg_date ) WHERE rownum <= #{pager.endRowNo} ) WHERE rnum >= #{pager.startRowNo}

mapper.update:
UPDATE member
SET point = (SELECT point FROM member WHERE member_id = #{memberId}) + #{point}
WHERE member_id = #{memberId}

mapper.select:
SELECT point
FROM member
WHERE member_id = #{memberId}
